import React from 'react';
import { useEffect, useState } from 'react';
import { Paragraph, Note, TextInput } from '@contentful/f36-components';
import { /* useCMA, */ useSDK } from '@contentful/react-apps-toolkit';

const Field = () => {
  const sdk = useSDK();
  /*
     To use the cma, inject it as follows.
     If it is not needed, you can remove the next line.
  */
  // const cma = useCMA();
  // If you only want to extend Contentful's default editing experience
  // reuse Contentful's editor components
  // -> https://www.contentful.com/developers/docs/extensibility/field-editors/
  console.log(sdk.entry)

  const [originalPrice, setOriginalPrice] = useState('');
  const [markdownPrice, setMarkdownPrice] = useState('');

  useEffect(() => {
    sdk.window.startAutoResizer();

    setOriginalPrice(sdk.entry.fields.originalPrice.getValue());
    setMarkdownPrice(sdk.entry.fields.markdownPrice.getValue());
  });

  return (
    <>
      {/* <Paragraph>Hello Entry Field Component (AppId: {sdk.ids.app})</Paragraph> */}
      <TextInput />
      <Note>
        Original Price: {originalPrice}
        Markdown Price: {markdownPrice}
      </Note>
    </>
  );
};

export default Field;
