import React from 'react';
import { useEffect, useState } from 'react';
import { Stack, TextInput } from '@contentful/f36-components';
import { /* useCMA, */ useSDK } from '@contentful/react-apps-toolkit';

const Field = () => {
  /*
     To use the cma, inject it as follows.
     If it is not needed, you can remove the next line.
  */
  // const cma = useCMA();
  // If you only want to extend Contentful's default editing experience
  // reuse Contentful's editor components
  // -> https://www.contentful.com/developers/docs/extensibility/field-editors/
  const sdk = useSDK();

  const [units, setUnits] = useState();
  const [sku, setSku] = useState(sdk.field.getValue().sku || '');
  const [originalPrice, setOriginalPrice] = useState(sdk.field.getValue().originalPrice || '');
  const [markdownPrice, setMarkdownPrice] = useState(sdk.field.getValue().markdownPrice || '');

  useEffect(() => {
    fetch('./prices.json', {
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json'
    }})
    .then((res) => {
      return res.json();
    })
    .then((data) => {
      setUnits(data);
    })
    .catch((error) => {
        console.log(error);
    });

    if (units && sku.length === 4 && units[sku]) {
      sdk.field.setValue(units[sku])
    }

  }, [sku]);

  return (
    <Stack flexDirection="column">
      <TextInput value={sku} onChange={(e) => { setSku(e.target.value) }} />
      <TextInput value={originalPrice} isDisabled />
      <TextInput isDisabled />
    </Stack>
  );
};

export default Field;
