import React from 'react';
import { FontWeightTokens } from '@contentful/f36-tokens';
import type {
  CommonProps,
  MarginProps,
  PolymorphicComponent,
  PolymorphicProps,
  ExpandProps,
} from '@contentful/f36-core';
import { Text } from '../Text';

const CAPTION_DEFAULT_TAG = 'span';

export interface CaptionInternalProps extends CommonProps, MarginProps {
  children?: React.ReactNode;
  fontWeight?: Extract<
    FontWeightTokens,
    'fontWeightNormal' | 'fontWeightMedium'
  >;
  isTruncated?: boolean;
  isWordBreak?: boolean;
}

export type CaptionProps<
  E extends React.ElementType = typeof CAPTION_DEFAULT_TAG,
> = PolymorphicProps<CaptionInternalProps, E>;

function _Caption<E extends React.ElementType = typeof CAPTION_DEFAULT_TAG>(
  {
    children,
    fontWeight = 'fontWeightNormal',
    testId = 'cf-ui-caption',
    ...otherProps
  }: CaptionProps<E>,
  ref: React.Ref<any>,
) {
  return (
    <Text
      as={CAPTION_DEFAULT_TAG}
      testId={testId}
      fontSize="fontSizeS"
      lineHeight="lineHeightS"
      fontColor="gray900"
      fontWeight={fontWeight}
      {...otherProps}
      ref={ref}
    >
      {children}
    </Text>
  );
}

_Caption.displayName = 'Caption';

export const Caption: PolymorphicComponent<
  ExpandProps<CaptionInternalProps>,
  typeof CAPTION_DEFAULT_TAG
> = React.forwardRef(_Caption);
import React from 'react';
import tokens, {
  FontSizeTokens,
  LineHeightTokens,
  FontWeightTokens,
  FontStackTokens,
  ColorTokens,
  LetterSpacingTokens,
} from '@contentful/f36-tokens';
import { css, cx } from 'emotion';
import {
  Box,
  type PolymorphicComponent,
  type CommonProps,
  type MarginProps,
  type PolymorphicProps,
  type ExpandProps,
} from '@contentful/f36-core';

export interface TextInternalProps extends CommonProps, MarginProps {
  children?: React.ReactNode;
  fontSize?: FontSizeTokens;
  lineHeight?: LineHeightTokens;
  letterSpacing?: LetterSpacingTokens;
  fontStack?: FontStackTokens;
  fontWeight?: FontWeightTokens;
  fontColor?: ColorTokens;
  isTruncated?: boolean;
  isWordBreak?: boolean;
}

const TEXT_DEFAULT_TAG = 'span';

function truncatedStyle() {
  return css({
    overflow: 'hidden',
    textOverflow: 'ellipsis',
    whiteSpace: 'nowrap',
    maxWidth: '100%',
  });
}

function wordBreakStyle() {
  return css({
    wordBreak: 'break-word',
  });
}

export type TextProps<E extends React.ElementType = typeof TEXT_DEFAULT_TAG> =
  PolymorphicProps<TextInternalProps, E>;

function _Text<E extends React.ElementType = typeof TEXT_DEFAULT_TAG>(
  {
    fontSize = 'fontSizeM',
    fontStack = 'fontStackPrimary',
    fontWeight = 'fontWeightNormal',
    fontColor = 'gray800',
    lineHeight = 'lineHeightM',
    letterSpacing = 'letterSpacingDefault',
    children,
    isTruncated,
    isWordBreak,
    as,
    className,
    margin = 'none',
    ...otherProps
  }: TextProps<E>,
  ref: React.Ref<any>,
) {
  const Element: React.ElementType = as || TEXT_DEFAULT_TAG;

  return (
    <Box
      {...otherProps}
      as={Element}
      className={cx(
        css({
          padding: 0,
          fontFamily: tokens[fontStack],
          fontWeight: tokens[fontWeight],
          color: tokens[fontColor],
          fontSize: tokens[fontSize],
          lineHeight: tokens[lineHeight],
          letterSpacing: tokens[letterSpacing],
        }),
        isTruncated ? truncatedStyle() : null,
        isWordBreak ? wordBreakStyle() : null,
        className,
      )}
      margin={margin}
      ref={ref}
    >
      {children}
    </Box>
  );
}

_Text.displayName = 'Text';

export const Text: PolymorphicComponent<
  ExpandProps<TextInternalProps>,
  typeof TEXT_DEFAULT_TAG
> = React.forwardRef(_Text);
import React from 'react';
import type {
  CommonProps,
  MarginProps,
  PolymorphicComponent,
  PolymorphicProps,
  ExpandProps,
} from '@contentful/f36-core';
import { Text } from '../Text';

const HEADING_DEFAULT_TAG = 'h1';

export type HeadingElement = 'h1' | 'h2' | 'h3' | 'h4' | 'h5' | 'h6';

export interface HeadingInternalProps extends CommonProps, MarginProps {
  as?: HeadingElement;
  children?: React.ReactNode;
  isTruncated?: boolean;
  isWordBreak?: boolean;
}

export type HeadingProps<
  E extends React.ElementType = typeof HEADING_DEFAULT_TAG,
> = PolymorphicProps<HeadingInternalProps, E>;

function _Heading<E extends React.ElementType = typeof HEADING_DEFAULT_TAG>(
  { children, testId = 'cf-ui-heading', ...otherProps }: HeadingProps<E>,
  ref: React.Ref<any>,
) {
  return (
    <Text
      as={HEADING_DEFAULT_TAG}
      testId={testId}
      marginBottom="spacingM"
      fontWeight="fontWeightDemiBold"
      fontColor="gray900"
      fontSize="fontSizeXl"
      lineHeight="lineHeightXl"
      {...otherProps}
      ref={ref}
    >
      {children}
    </Text>
  );
}

_Heading.displayName = 'Heading';

export const Heading: PolymorphicComponent<
  ExpandProps<HeadingInternalProps>,
  typeof HEADING_DEFAULT_TAG
> = React.forwardRef(_Heading);
