import { cx } from 'emotion';
import React from 'react';
import {
  Flex,
  Grid,
  type CommonProps,
  type PropsWithHTMLElement,
  type ExpandProps,
} from '@contentful/f36-core';
import { Button } from '@contentful/f36-button';
import { Heading, Paragraph, Text } from '@contentful/f36-typography';
import {
  CheckCircleIcon,
  CloseIcon,
  ErrorCircleIcon,
  InfoCircleIcon,
  WarningIcon,
  DiamondIcon,
} from '@contentful/f36-icons';
import { Icon } from '@contentful/f36-icon';

import { getNoteStyles } from './Note.styles';

const icons = {
  primary: InfoCircleIcon,
  positive: CheckCircleIcon,
  negative: ErrorCircleIcon,
  warning: WarningIcon,
  neutral: InfoCircleIcon,
  premium: DiamondIcon,
};

export type NoteVariant =
  | 'negative'
  | 'positive'
  | 'primary'
  | 'warning'
  | 'neutral'
  | 'premium';

export type NoteInternalProps = CommonProps & {
  /**
   * Determines style variation of Note component
   */
  variant?: NoteVariant;
  /**
   * Sets title in the Note
   */
  title?: React.ReactNode;
  /**
   * Children of Note
   */
  children?: React.ReactNode | string;
  /**
   * Defines if the close button should be rendered
   * @default false
   */
  withCloseButton?: boolean;
  /**
   * Expects any of the icon components to override the default Note variant icon
   */
  icon?: React.ReactElement;
  /**
   * Callback for handling closing
   */
  onClose?: React.MouseEventHandler<HTMLButtonElement>;
};
export type NoteProps = PropsWithHTMLElement<NoteInternalProps, 'article'>;

/**
 * @description: Note provides context and information about a situation or action.
 */
export const Note = React.forwardRef<HTMLElement, ExpandProps<NoteProps>>(
  (props, ref) => {
    const {
      children,
      className,
      withCloseButton = false,
      variant = 'primary',
      onClose,
      testId = 'cf-ui-note',
      title,
      icon,
      ...otherProps
    } = props;

    const styles = getNoteStyles();

    const iconSize = title ? 'medium' : 'small';
    const iconVariant = variant === 'neutral' ? 'muted' : variant;

    const iconContent = (icon: React.ReactElement) =>
      React.cloneElement(icon, {
        size: iconSize,
        variant: iconVariant,
      });

    return (
      <Grid
        {...otherProps}
        columns={withCloseButton ? 'auto 1fr 24px' : 'auto 1fr'} // 24px is the width of the close button
        as="article"
        className={cx(styles.container({ variant }), className)}
        testId={testId}
        ref={ref}
        padding="spacingM"
      >
        {icon ? (
          iconContent(icon)
        ) : (
          <Icon as={icons[variant]} variant={iconVariant} size={iconSize} />
        )}
        <Flex flexDirection="column">
          {title && (
            <Heading
              as="h2"
              className={styles.title}
              marginBottom={!children ? 'none' : 'spacingS'}
            >
              {title}
            </Heading>
          )}
          {children && (
            <Text
              as="div"
              lineHeight="lineHeightM"
              className={styles.description}
            >
              {typeof children === 'string' ? (
                <Paragraph marginBottom="none">{children}</Paragraph>
              ) : (
                children
              )}
            </Text>
          )}
        </Flex>
        {withCloseButton && (
          <Button
            variant="transparent"
            startIcon={<CloseIcon className={styles.closeIcon} />}
            onClick={onClose}
            testId={`${testId}-close`}
            aria-label="Dismiss"
            className={styles.close}
          />
        )}
      </Grid>
    );
  },
);

Note.displayName = 'Note';
import { css } from 'emotion';
import type { CSSObject } from '@emotion/serialize';
import tokens from '@contentful/f36-tokens';

import type { NoteProps, NoteVariant } from './Note';

const variantToStyles = (variant: NoteVariant): CSSObject => {
  switch (variant) {
    case 'primary':
      return {
        backgroundColor: tokens.blue100,
        borderColor: tokens.blue300,

        a: {
          color: tokens.blue700,
        },
      };
    case 'positive':
      return {
        backgroundColor: tokens.green100,
        borderColor: tokens.green300,

        a: {
          color: tokens.blue700,
        },
      };
    case 'negative':
      return {
        backgroundColor: tokens.red100,
        borderColor: tokens.red300,

        a: {
          color: tokens.blue700,
        },
      };
    case 'warning':
      return {
        backgroundColor: tokens.orange100,
        borderColor: tokens.orange300,

        a: {
          color: tokens.blue700,
        },
      };
    case 'neutral':
      return {
        backgroundColor: tokens.gray100,
        borderColor: tokens.gray300,

        a: {
          color: tokens.blue700,
        },
      };
    case 'premium':
      return {
        backgroundColor: tokens.purple100,
        borderColor: tokens.purple300,

        a: {
          color: tokens.blue700,
        },
      };
    default:
      return {};
  }
};

export const getNoteStyles = () => {
  return {
    container: ({ variant }: Pick<NoteProps, 'variant'>) =>
      css({
        position: 'relative',
        borderRadius: tokens.borderRadiusMedium,
        border: '1px solid',
        ...variantToStyles(variant),
      }),
    title: css({
      color: tokens.gray800,
      fontSize: tokens.fontSizeL,
      lineHeight: tokens.lineHeightL,
    }),
    description: css({
      color: tokens.gray700,
    }),
    close: css({
      position: 'absolute',
      top: tokens.spacingXs,
      right: tokens.spacingXs,
      padding: tokens.spacingXs,
      '&:hover, &:active': {
        backgroundColor: 'transparent',
      },
    }),
    closeIcon: css({
      fill: tokens.gray600,
    }),
  };
};
