import { cx } from 'emotion';
import React from 'react';
import { Box, Stack, type ExpandProps } from '@contentful/f36-core';
import getStyles from './ButtonGroup.styles';
import type { ButtonGroupProps } from './types';

function _ButtonGroup(
  props: ExpandProps<ButtonGroupProps>,
  ref: React.Ref<HTMLDivElement>,
) {
  const {
    variant = 'merged',
    withDivider,
    testId = 'cf-ui-button-group',
    children,
    className,
    spacing,
  } = props;
  const styles = getStyles({ withDivider });

  if (variant === 'spaced') {
    return (
      <Stack
        className={className}
        isInline
        flexDirection="row"
        testId={testId}
        ref={ref}
        spacing={spacing}
      >
        {children}
      </Stack>
    );
  }

  return (
    <Box
      testId={testId}
      ref={ref}
      className={cx(styles.buttonGroup, className)}
    >
      {React.Children.map(children, (child, key) => {
        if (!child) {
          return null;
        }
        return React.cloneElement(child as React.ReactElement, {
          key,
          className: cx(
            styles.groupContent,
            (child as React.ReactElement).props.className,
          ),
        });
      })}
    </Box>
  );
}

_ButtonGroup.displayName = 'ButtonGroup';

export const ButtonGroup = React.forwardRef(_ButtonGroup);
import { css } from 'emotion';
import tokens from '@contentful/f36-tokens';
import { hexToRGBA } from '@contentful/f36-utils';
import type { CSSObject } from '@emotion/serialize';
import type { GetStyleArguments } from './types';

export default ({ withDivider }: GetStyleArguments) => {
  const dividerStyle = getDividerStyle(withDivider);

  return {
    buttonGroup: css({
      display: 'inline-flex',
      position: 'relative',
    }),
    groupContent: css({
      borderRadius: '0 !important',
      marginRight: '-1px',
      '&:first-child': {
        borderBottomLeftRadius: `${tokens.borderRadiusMedium} !important`,
        borderTopLeftRadius: `${tokens.borderRadiusMedium} !important`,
      },
      '&:last-child': {
        borderBottomRightRadius: `${tokens.borderRadiusMedium} !important`,
        borderTopRightRadius: `${tokens.borderRadiusMedium} !important`,
        marginRight: 0,
      },
      '&:focus': {
        zIndex: tokens.zIndexDefault,
      },
      ...dividerStyle,
    }),
  };
};

const getDividerStyle = (withDivider: boolean): CSSObject => {
  if (!withDivider) return {};

  const divider = `1px solid ${hexToRGBA(tokens.colorWhite, 0.2)}`;

  return {
    borderTop: 'none',
    borderBottom: 'none',
    '&:not(:first-child,:focus-visible)': {
      borderLeft: divider,
    },
    '&:not(:last-child,:focus-visible)': {
      borderRight: divider,
    },
  };
};
import React from 'react';
import { cx } from 'emotion';
import type { CommonProps, ExpandProps } from '@contentful/f36-core';
import { Button } from '../Button';
import getStyles from './ToggleButton.styles';
import { ButtonSize } from '../types';

export interface ToggleButtonProps extends CommonProps {
  /**
   * Applies active styles
   * @default false
   */
  isActive?: boolean;
  /**
   * Disabled interaction and applies disabled styles
   * @default false
   */
  isDisabled?: boolean;
  /**
   * Expects any of the icon components
   */
  icon?: React.ReactElement;
  /**
   * Function triggered when the toggle button is clicked.
   */
  onToggle: () => void;

  /**
   * Determines size variation of Button component
   * @default medium
   */
  size?: ButtonSize;

  /**
   * Aria label is required when using icon only
   */
  'aria-label'?: string;

  children?: React.ReactNode;
}

function _ToggleButton(props: ExpandProps<ToggleButtonProps>, ref) {
  const {
    testId = 'cf-ui-toggle-button',
    children,
    className,
    isDisabled = false,
    isActive = false,
    icon,
    onToggle,
    size = 'medium',
    ...otherProps
  } = props;

  const styles = getStyles({ isActive, isDisabled });

  const handleToggle = () => {
    if (!isDisabled && onToggle) {
      onToggle();
    }
  };

  return (
    <Button
      testId={testId}
      type="button"
      ref={ref}
      size={size}
      onClick={handleToggle}
      className={cx(styles.toggleButton, className)}
      startIcon={icon}
      isDisabled={isDisabled}
      aria-pressed={isActive}
      data-state={isActive ? 'on' : 'off'}
      {...otherProps}
    >
      {children}
    </Button>
  );
}

_ToggleButton.displayName = 'ToggleButton';

export const ToggleButton = React.forwardRef(_ToggleButton);
import React from 'react';
import { cx } from 'emotion';
import {
  Flex,
  Box,
  type PolymorphicProps,
  type PolymorphicComponent,
  type ExpandProps,
} from '@contentful/f36-core';
import { useDensity } from '@contentful/f36-utils';
import { Spinner } from '@contentful/f36-spinner';

import type { ButtonInternalProps } from '../types';
import { getStyles } from './Button.styles';

const BUTTON_DEFAULT_TAG = 'button';

export type ButtonProps<
  E extends React.ElementType = typeof BUTTON_DEFAULT_TAG,
> = PolymorphicProps<ButtonInternalProps, E, 'disabled'>;

function _Button<E extends React.ElementType = typeof BUTTON_DEFAULT_TAG>(
  props: ButtonProps<E>,
  ref: React.Ref<any>,
) {
  const styles = getStyles();
  const {
    as = BUTTON_DEFAULT_TAG,
    children,
    className,
    testId = 'cf-ui-button',
    variant = 'secondary',
    size = 'medium',
    startIcon,
    endIcon,
    isActive,
    isDisabled,
    isLoading,
    isFullWidth,
    style,
    ...otherProps
  } = props;
  const density = useDensity();

  const rootClassNames = cx(
    styles.button({
      variant,
      size,
      density,
      isActive,
      isDisabled,
      isFullWidth,
    }),
    className,
  );

  const iconContent = (icon) => {
    const defaultIconColor: {
      [Property in ButtonInternalProps['variant']]: string;
    } = {
      primary: 'white',
      secondary: 'secondary',
      positive: 'white',
      negative: 'negative',
      transparent: 'secondary',
    };

    return (
      !isLoading && (
        <Flex
          as="span"
          className={styles.buttonIcon({ hasChildren: !!children, variant })}
        >
          {React.cloneElement(icon, {
            size: icon.props.size ?? `${size === 'large' ? 'medium' : 'small'}`,
            // we want to allow variants for icons for transparent buttons
            variant:
              (variant === 'transparent' && icon.props.variant) ||
              defaultIconColor[variant],
          })}
        </Flex>
      )
    );
  };

  const commonContent = (
    <>
      {startIcon && iconContent(startIcon)}
      {children && (
        <Box as="span" display="block" className={styles.buttonContent}>
          {children}
        </Box>
      )}
      {endIcon && iconContent(endIcon)}
      {isLoading && (
        <Box
          as="span"
          marginLeft={children || !isLoading ? 'spacingXs' : 'none'}
        >
          <Spinner
            customSize={18}
            variant={
              variant === 'secondary' ||
              variant === 'negative' ||
              variant === 'transparent'
                ? 'default'
                : 'white'
            }
          />
        </Box>
      )}
    </>
  );

  const commonProps = {
    ['data-test-id']: testId,
    className: rootClassNames,
    ref: ref,
    style,
  };

  if (as === 'a') {
    return (
      <a {...otherProps} {...commonProps} disabled={isDisabled}>
        {commonContent}
      </a>
    );
  }

  return (
    <button
      type="button"
      {...otherProps}
      {...commonProps}
      disabled={isDisabled}
    >
      {commonContent}
    </button>
  );
}

_Button.displayName = 'Button';

/**
 * @description: Buttons communicate the action that will occur when the user clicks it
 */
export const Button: PolymorphicComponent<
  ExpandProps<ButtonInternalProps>,
  typeof BUTTON_DEFAULT_TAG,
  'disabled'
> = React.forwardRef(_Button);
import { css } from 'emotion';
import type { CSSObject } from '@emotion/serialize';
import tokens from '@contentful/f36-tokens';
import { ButtonSize, ButtonVariant, ButtonStylesProps } from '../types';
import { hexToRGBA, type Density } from '@contentful/f36-utils';

const variantActiveStyles = (variant: ButtonVariant): CSSObject => {
  switch (variant) {
    case 'primary':
      return { backgroundColor: tokens.blue700 };
    case 'secondary':
      return { backgroundColor: tokens.gray200 };
    case 'positive':
      return { backgroundColor: tokens.green700 };
    case 'negative':
      return { backgroundColor: tokens.gray200 };
    case 'transparent':
      return { backgroundColor: hexToRGBA(tokens.gray900, 0.1) };
    default:
      return {};
  }
};

const variantToStyles = (variant: ButtonVariant): CSSObject => {
  switch (variant) {
    case 'primary':
      return {
        color: tokens.colorWhite,
        backgroundColor: tokens.blue500,
        borderColor: 'transparent',
        '&:hover': {
          backgroundColor: tokens.blue600,
          color: tokens.colorWhite,
        },
        '&:active': variantActiveStyles(variant),
        '&:focus-visible': {
          boxShadow: tokens.glowPrimary,
        },
      };
