import { MenuDivider } from '@contentful/f36-menu';
import { MenuSectionTitle } from '@contentful/f36-menu';
import { Navbar as OriginalNavbar } from './Navbar';

import { NavbarAccount, NavbarAccountSkeleton } from './NavbarAccount';
import { NavbarItem, NavbarItemSkeleton } from './NavbarItem';
import { NavbarMenuItem, NavbarMenuItemSkeleton } from './NavbarMenuItem';
import { NavbarSwitcher, NavbarSwitcherSkeleton } from './NavbarSwitcher';

import { NavbarSearch } from './NavbarSearch/NavbarSearch';
import { NavbarSwitcherItem } from './NavbarSwitcherItem/NavbarSwitcherItem';
import { NavbarHelp } from './NavbarHelp/NavbarHelp';
import { NavbarBadge } from './NavbarBadge/NavbarBadge';
import { NavbarTopbarItem } from './NavbarTopbarItem/NavbarTopbarItem';

type CompoundNavbar = typeof OriginalNavbar & {
  Item: typeof NavbarItem;
  ItemSkeleton: typeof NavbarItemSkeleton;
  MenuItem: typeof NavbarMenuItem;
  MenuItemSkeleton: typeof NavbarMenuItemSkeleton;
  MenuDivider: typeof MenuDivider;
  MenuSectionTitle: typeof MenuSectionTitle;
  Switcher: typeof NavbarSwitcher;
  SwitcherSkeleton: typeof NavbarSwitcherSkeleton;
  SwitcherItem: typeof NavbarSwitcherItem;
  Search: typeof NavbarSearch;
  Help: typeof NavbarHelp;
  Account: typeof NavbarAccount;
  AccountSkeleton: typeof NavbarAccountSkeleton;
  Badge: typeof NavbarBadge;
  TopbarItem: typeof NavbarTopbarItem;
};

export const Navbar = OriginalNavbar as CompoundNavbar;
Navbar.Item = NavbarItem;
Navbar.ItemSkeleton = NavbarItemSkeleton;
Navbar.MenuItem = NavbarMenuItem;
Navbar.MenuItemSkeleton = NavbarMenuItemSkeleton;
Navbar.MenuDivider = MenuDivider;
Navbar.MenuSectionTitle = MenuSectionTitle;
Navbar.Switcher = NavbarSwitcher;
Navbar.SwitcherSkeleton = NavbarSwitcherSkeleton;
Navbar.SwitcherItem = NavbarSwitcherItem;
Navbar.Search = NavbarSearch;
Navbar.Help = NavbarHelp;
Navbar.Account = NavbarAccount;
Navbar.AccountSkeleton = NavbarAccountSkeleton;
Navbar.Badge = NavbarBadge;
Navbar.TopbarItem = NavbarTopbarItem;
import {
  Box,
  type CommonProps,
  type ExpandProps,
  Flex,
} from '@contentful/f36-core';
import React from 'react';
import { getNavbarStyles } from './Navbar.styles';

type NavbarOwnProps = CommonProps & {
  children?: React.ReactNode;
  account?: React.ReactNode;
  search?: React.ReactNode;
  switcher?: React.ReactNode;
  help?: React.ReactNode;
  badge?: React.ReactNode;
  /**
   * Items that will be rendered on the bottom-right of the navbar.
   * Useful for separating other navigation items from main ones, (e.g. separating "Settings" from all other navigation items).
   */
  bottomRightItems?: React.ReactNode;
  /**
   * Items that will be rendered on the top-right of the navbar.
   * Useful for providing additional context or actions to the user (e.g. a Feedback form link).
   */
  topRightItems?: React.ReactNode;
  /**
   * Defines the max-width of the content inside the navbar.
   * @default '100%'
   */
  contentMaxWidth?: string;
};

// expose only the HTML props that are needed to not pollute the API
type NavbarHTMLElementProps = Pick<React.ComponentPropsWithoutRef<'div'>, 'id'>;

export type NavbarProps = NavbarHTMLElementProps & NavbarOwnProps;

function _Navbar(props: ExpandProps<NavbarProps>, ref: React.Ref<HTMLElement>) {
  const {
    children,
    account,
    search,
    switcher,
    help,
    badge,
    bottomRightItems,
    topRightItems,
    contentMaxWidth = '100%',
    testId = 'cf-ui-navbar',
    ...otherProps
  } = props;
  const styles = getNavbarStyles(contentMaxWidth);

  return (
    <Box {...otherProps} ref={ref} testId={testId}>
      <Flex className={styles.containerTop}>
        <Flex
          className={styles.containerTopContent}
          justifyContent="space-between"
        >
          <Flex>{switcher}</Flex>
          <Flex alignItems="center" gap="spacingXs">
            {topRightItems}
            {badge}
            {search}
            {help}
            {account}
          </Flex>
        </Flex>
      </Flex>

      <Flex className={styles.containerBottom}>
        <Flex
          className={styles.containerBottomContent}
          justifyContent="space-between"
        >
          <Flex as="nav" aria-label="Main Navigation" alignItems="stretch">
            {children}
          </Flex>
          {bottomRightItems && <Flex>{bottomRightItems}</Flex>}
        </Flex>
      </Flex>
    </Box>
  );
}

export const Navbar = React.forwardRef(_Navbar);
import { css } from 'emotion';
import tokens from '@contentful/f36-tokens';
import { mqs } from './utils.styles';

export const getNavbarStyles = (maxWidth: string) => ({
  containerTop: css({
    justifyContent: 'center',
    backgroundColor: '#0C141C',
  }),
  containerBottom: css({
    justifyContent: 'center',
    borderTop: `1px solid #21262D`,
    backgroundColor: '#161B22',
  }),
  containerTopContent: css({
    width: '100%',
    maxWidth: maxWidth,
    padding: `${tokens.spacingXs}`,
    minHeight: '2.5rem',
    [mqs.medium]: {
      padding: `${tokens.spacingXs} ${tokens.spacingM}`,
    },
  }),
  containerBottomContent: css({
    width: '100%',
    maxWidth: maxWidth,
    padding: 0,
    minHeight: '2.5rem',
    overflow: 'auto',
    [mqs.medium]: {
      padding: `0 ${tokens.spacing2Xs}`,
    },
  }),
});
import tokens from '@contentful/f36-tokens';

type screens = 'medium' | 'large' | 'xlarge';
type mediaqueries = Record<screens, string>;
export const mqs: mediaqueries = {
  medium: '@media (min-width: 480px)',
  large: '@media (min-width: 768px)',
  xlarge: '@media (min-width: 1920px)',
};

export const getGlowOnFocusStyles = (shadow: string = tokens.glowPrimary) => ({
  '&:focus': {
    boxShadow: shadow,
  },
  '&:focus:not(:focus-visible)': {
    boxShadow: 'unset',
  },
  '&:focus-visible': {
    boxShadow: shadow,
  },
});
import React from 'react';
import { cx } from 'emotion';
import { getNavbarAccountStyles } from './NavbarAccount.styles';
import {
  Flex,
  type PropsWithHTMLElement,
  type CommonProps,
  type ExpandProps,
} from '@contentful/f36-core';
import { NavbarMenu } from '../NavbarMenu/NavbarMenu';

type NavbarAccountOwnProps = CommonProps & {
  children: React.ReactNode;
  username: string;
  avatar: string;
  hasNotification?: boolean;
  /**
   * @default 'warning'
   */
  notificationVariant?: 'warning' | 'negative';
};

export type NavbarAccountProps = PropsWithHTMLElement<
  NavbarAccountOwnProps,
  'button'
>;

function _NavbarAccount(
  props: ExpandProps<NavbarAccountProps>,
  ref: React.Ref<HTMLButtonElement>,
) {
  const {
    children,
    className,
    testId = 'cf-ui-navbar-account-trigger',
    avatar,
    username,
    hasNotification,
    notificationVariant = 'warning',
    ...otherProps
  } = props;
  const styles = getNavbarAccountStyles();

  return (
    <NavbarMenu
      trigger={
        <Flex
          {...otherProps}
          as="button"
          ref={ref}
          className={cx(styles.root, className)}
          testId={testId}
        >
          <img
            src={avatar}
            alt={`Avatar for user ${username}`}
            className={styles.avatar}
          />
          {hasNotification ? (
            <span className={styles.notificationIcon(notificationVariant)} />
          ) : null}
        </Flex>
      }
    >
      {children}
    </NavbarMenu>
  );
}

export const NavbarAccount = React.forwardRef(_NavbarAccount);
import { css } from 'emotion';
import tokens from '@contentful/f36-tokens';
import { NavbarAccountProps } from './NavbarAccount';
import { getGlowOnFocusStyles } from '../utils.styles';

export const getNavbarAccountStyles = () => ({
  root: css(
    {
      // default button reset styles
      margin: 0,
      padding: 0,
      background: 'none',
      border: 'none',

      cursor: 'pointer',
      position: 'relative',
      alignItems: 'center',
      justifyContent: 'center',
      gap: tokens.spacing2Xs,
      outline: 'none',
      borderRadius: '50%',
    },
    getGlowOnFocusStyles(),
  ),
  avatar: css({
    borderRadius: '50%',
    display: 'block',
    height: '24px',
    width: '24px',
  }),
  notificationIcon: (variant: NavbarAccountProps['notificationVariant']) =>
    css({
      position: 'absolute',
      top: 0,
      right: 0,
      height: tokens.spacingS,
      width: tokens.spacingS,
      borderRadius: '50%',
      border: `2px solid ${tokens.gray900}`,
      backgroundColor:
        variant === 'negative' ? tokens.colorNegative : tokens.colorWarning,
      transform: 'translate(30%, -30%)',
    }),
});
import React from 'react';
import { Menu, type MenuListProps, type MenuProps } from '@contentful/f36-menu';
import { getNavbarMenuStyles } from './NavbarMenu.styles';

export type NavbarMenuProps = {
  trigger: React.ReactNode;
  children?: React.ReactNode;
} & Pick<MenuListProps, 'testId'> &
  Pick<MenuProps, 'onOpen' | 'onClose'>;

export const NavbarMenu = (props: NavbarMenuProps) => {
  const {
    trigger,
    children,
    testId = 'cf-ui-navbar-menu-list',
    onOpen,
    onClose,
  } = props;
  const styles = getNavbarMenuStyles();

  return (
    <Menu onOpen={onOpen} onClose={onClose}>
      <Menu.Trigger>{trigger}</Menu.Trigger>
      <Menu.List className={styles.menuList} testId={testId}>
        {children}
      </Menu.List>
    </Menu>
  );
};
import { css } from 'emotion';

export const getNavbarMenuStyles = () => ({
  menuList: css({
    minWidth: '250px',
  }),
});
import React from 'react';
import { SkeletonContainer, SkeletonImage } from '@contentful/f36-skeleton';
import tokens from '@contentful/f36-tokens';

export function NavbarAccountSkeleton({ ariaLabel }: { ariaLabel?: string }) {
  return (
    <SkeletonContainer
      svgWidth={24}
      svgHeight={24}
      ariaLabel={ariaLabel}
      backgroundColor={tokens.gray800}
      foregroundColor={tokens.gray700}
    >
      <SkeletonImage width={24} height={24} radiusX={12} radiusY={12} />
    </SkeletonContainer>
  );
}
import React from 'react';
import { cx } from 'emotion';
import { getNavbarItemStyles } from './NavbarItem.styles';
import { NavbarMenu, type NavbarMenuProps } from '../NavbarMenu/NavbarMenu';
import {
  NavbarItemIcon,
  type NavbarItemIconProps,
} from '../NavbarItemIcon/NavbarItemIcon';
import { ArrowDownIcon } from '../icons';
import type {
  CommonProps,
  ExpandProps,
  PolymorphicComponent,
  PolymorphicProps,
} from '@contentful/f36-core';

const NAVBAR_ITEM_DEFAULT_TAG = 'button';

type NavbarItemTriggerProps = CommonProps & {
  title: string;
  icon?: NavbarItemIconProps['icon'];
  isActive?: boolean;
  as?: React.ElementType;
};

type NavbarItemAsMenuProps = NavbarItemTriggerProps &
  Pick<NavbarMenuProps, 'onOpen' | 'onClose'> & {
    children: React.ReactNode;
  };
type NavbarItemAsItemProps = NavbarItemTriggerProps & {
  children?: never;
  onOpen?: never;
  onClose?: never;
};
type NavbarItemOwnProps = NavbarItemAsItemProps | NavbarItemAsMenuProps;

export type NavbarItemProps<
  E extends React.ElementType = typeof NAVBAR_ITEM_DEFAULT_TAG,
> = PolymorphicProps<NavbarItemOwnProps, E>;

function _NavbarItem(
  props: NavbarItemProps<React.ElementType>,
  ref: React.Ref<any>,
) {
  const {
    as: Comp = NAVBAR_ITEM_DEFAULT_TAG,
    icon,
    title,
    children,
    className,
    isActive,
    testId = 'cf-ui-navbar-item',
    onOpen,
    onClose,
    ...otherProps
  } = props;
  const styles = getNavbarItemStyles();

  const item = (
    <Comp
      {...otherProps}
      ref={ref}
      data-test-id={testId}
      className={cx(styles.root, isActive && styles.isActive, className)}
    >
      {icon ? <NavbarItemIcon icon={icon} variant="white" /> : null}
      <span>{title}</span>
      {isNavbarItemHasMenu(props) && (
        <ArrowDownIcon className={styles.dropdownIcon} />
      )}
    </Comp>
  );

  if (isNavbarItemHasMenu(props)) {
    return (
      <NavbarMenu
        trigger={item}
        testId={testId}
        onOpen={onOpen}
        onClose={onClose}
      >
        {children}
      </NavbarMenu>
    );
  }

  return item;
}

const isNavbarItemHasMenu = <E extends React.ElementType>(
  props: NavbarItemProps<E>,
): props is PolymorphicProps<NavbarItemAsMenuProps, E> =>
  Boolean(props.children);

export const NavbarItem: PolymorphicComponent<
  ExpandProps<NavbarItemOwnProps>,
  typeof NAVBAR_ITEM_DEFAULT_TAG
> = React.forwardRef(_NavbarItem);
import { css } from 'emotion';
import tokens from '@contentful/f36-tokens';
import { hexToRGBA } from '@contentful/f36-utils';
import { getGlowOnFocusStyles, mqs } from '../utils.styles';

export const getNavbarItemActiveStyles = () =>
  css({
    '&': {
      color: tokens.colorWhite,
      '& svg': {
        fill: tokens.colorWhite,
      },
    },

    '&::after': {
      content: '""',
