      },

      '&:active::before': {
        backgroundColor: `rgba(255, 255, 255, 0.1)`,
      },
      '&:disabled': {
        cursor: 'auto',
      },

      '& svg': {
        fill: hexToRGBA(tokens.gray300, 0.8),
        transition: `fill ${tokens.transitionDurationShort} ${tokens.transitionEasingCubicBezier}`,

        '&:first-child': {
          display: 'none',
          [mqs.large]: {
            display: 'block',
          },
        },
      },
      '& > svg, & > span': {
        zIndex: tokens.zIndexDefault,
      },
    },
    getGlowOnFocusStyles(`inset ${tokens.glowPrimary}`),
  ),
  isActive: getNavbarItemActiveStyles(),
  dropdownIcon: css({
    paddingLeft: tokens.spacing2Xs,
  }),
});

export const getNavbarItemSkeletonStyles = () => ({
  root: css(commonItemStyles),
});
import React from 'react';
import type { IconProps } from '@contentful/f36-icons';
import { getNavbarItemIconStyles } from './NavbarItemIcon.styles';
import { cx } from 'emotion';

export type NavbarItemIconProps = {
  icon: React.ReactElement<IconProps>;
} & Pick<IconProps, 'variant'>;

export const NavbarItemIcon = (props: NavbarItemIconProps) => {
  const { icon, variant } = props;
  const styles = getNavbarItemIconStyles();

  return React.cloneElement(icon, {
    className: cx(icon.props.className, styles.root),
    size: icon.props.size ?? 'tiny',
    variant: icon.props.variant ?? variant,
  });
};
