import React from 'react';
import { cx } from 'emotion';
import {
  Box,
  type CommonProps,
  type PropsWithHTMLElement,
  type ExpandProps,
} from '@contentful/f36-core';

import type { BadgeSize, BadgeVariant } from '../types';
import { getBadgeStyles } from './Badge.styles';
import { Caption } from '@contentful/f36-typography';

export type BadgeInternalProps = CommonProps & {
  /**
   * Determines the variation of the component
   * @default primary
   */
  variant?: BadgeVariant;
  children: React.ReactNode;
  /**
   * Sets the size of the component
   * @default default
   */
  size?: BadgeSize;
  /**
   * Expects any of the icon components. Renders the icon aligned to the start
   */
  startIcon?: React.ReactNode;
  /**
   * Expects any of the icon components. Renders the icon aligned to the end
   */
  endIcon?: React.ReactNode;
};

export type BadgeProps = PropsWithHTMLElement<BadgeInternalProps, 'div'>;

export const Badge = React.forwardRef<HTMLDivElement, ExpandProps<BadgeProps>>(
  (props, ref) => {
    const styles = getBadgeStyles();
    const {
      children,
      variant = 'primary',
      size = 'default',
      testId = 'cf-ui-badge',
      startIcon,
      endIcon,
      className,
      ...otherProps
    } = props;

    const iconContent = (icon) =>
      React.cloneElement(icon, {
        size: 'tiny',
        className: cx(styles.badgeIcon, icon.props.className, {
          [styles.badgeIconCustomTiny]: size === 'small',
        }),
        variant: variant === 'primary-filled' ? 'white' : variant,
      });

    return (
      <Box
        as="div"
        testId={testId}
        display="inline-flex"
        className={cx(styles.badge({ variant, size }), className)}
        {...otherProps}
        ref={ref}
      >
        {startIcon && iconContent(startIcon)}
        <Caption
          fontWeight="fontWeightMedium"
          isTruncated
          className={styles.badgeText}
        >
          {children}
        </Caption>
        {endIcon && iconContent(endIcon)}
      </Box>
    );
  },
);

Badge.displayName = 'Badge';
import tokens from '@contentful/f36-tokens';
import { css } from 'emotion';
import type { BadgeVariant, BadgeSize, BadgeStylesProps } from '../types';
import type { CSSObject } from '@emotion/serialize';

const variantToStyles = ({ variant }: { variant: BadgeVariant }): CSSObject => {
  switch (variant) {
    case 'positive':
      return {
        color: tokens.green600,
        backgroundColor: tokens.green200,
      };
    case 'primary':
      return {
        color: tokens.blue600,
        backgroundColor: tokens.blue200,
      };
    case 'negative':
      return {
        color: tokens.red600,
        backgroundColor: tokens.red200,
      };
    case 'warning':
      return {
        color: tokens.orange600,
        backgroundColor: tokens.orange200,
      };
    case 'secondary':
      return {
        color: tokens.gray700,
        backgroundColor: tokens.gray200,
      };
    case 'primary-filled':
      return {
        color: tokens.colorWhite,
        backgroundColor: tokens.blue600,
      };
    case 'featured':
      return {
        color: tokens.purple600,
        backgroundColor: tokens.purple200,
      };
    default:
      return {};
  }
};

const sizeToStyles = ({ size }: { size: BadgeSize }): CSSObject => {
  switch (size) {
    case 'small':
      return {
        padding: `0 ${tokens.spacing2Xs}`,
        lineHeight: tokens.lineHeightS,
        maxHeight: tokens.lineHeightS,
      };
    default:
      return {
        padding: `0 ${tokens.spacingXs}`,
        lineHeight: tokens.lineHeightM,
        maxHeight: tokens.lineHeightM,
      };
  }
};

export const getBadgeStyles = () => ({
  badge: ({ variant, size }: BadgeStylesProps) =>
    css({
      columnGap: tokens.spacing2Xs,
      alignItems: 'center',
      borderRadius: tokens.borderRadiusSmall,
      overflow: 'hidden',
      verticalAlign: 'middle',
      ...variantToStyles({ variant }),
      ...sizeToStyles({ size }),
    }),
  badgeIcon: css({
    flexShrink: 0,
  }),
  badgeIconCustomTiny: css({
    width: '0.875rem',
    height: '0.875rem',
  }),
  badgeText: css({
    color: 'currentcolor',
    lineHeight: 'inherit',
    textTransform: 'lowercase',
    '::first-letter': {
      textTransform: 'uppercase',
    },
  }),
});
import React from 'react';
import type {
  EntityStatus,
  ExpandProps,
  PickUnion,
} from '@contentful/f36-core';
import { ClockIcon } from '@contentful/f36-icons';

import { Badge, type BadgeProps } from '../Badge/Badge';
import type { BadgeSize, BadgeVariant } from '../types';

const statusMap: { [key in EntityStatus]: BadgeVariant } = {
  published: 'positive',
  draft: 'warning',
  archived: 'secondary',
  changed: 'primary',
  deleted: 'negative',
  new: 'primary-filled',
};

type BadgeSizeWithIsScheduledProp =
  | {
      /**
       * Sets the size of the component
       * @default default
       */
      size?: Exclude<BadgeSize, 'small'>;
      /**
       * Indicates that the entity that the badge is related to has a scheduled action
       */
      isScheduled?: boolean;
    }
  | {
      /**
       * Sets the size of the component
       * @default default
       */
      size: PickUnion<BadgeSize, 'small'>;
      // We use discriminative union typing, so in case the size is set as small we don't allow isScheduled
      isScheduled?: never;
    };

export type EntityStatusBadgeProps = Omit<
  BadgeProps,
  'children' | 'endIcon' | 'size' | 'startIcon' | 'variant'
> &
  BadgeSizeWithIsScheduledProp & {
    entityStatus: EntityStatus;
  };

function EntityStatusBadge(
  props: EntityStatusBadgeProps,
  ref: React.Ref<HTMLDivElement>,
) {
  const {
    entityStatus,
    isScheduled = undefined,
    size = 'default',
    ...otherProps
  } = props;
  const withClockIcon = isScheduled && size === 'default';
  const variant = statusMap[entityStatus];

  return (
    <Badge
      {...otherProps}
      {...(withClockIcon
        ? { size, startIcon: <ClockIcon testId="schedule-icon" /> }
        : { size })}
      variant={variant}
      ref={ref}
    >
      {entityStatus}
    </Badge>
  );
}

EntityStatusBadge.displayName = 'EntityStatusBadge';

const _EntityStatusBadge = React.forwardRef<
  HTMLDivElement,
  ExpandProps<EntityStatusBadgeProps>
>(EntityStatusBadge);
export { _EntityStatusBadge as EntityStatusBadge };
